FunctionOracle.source:
create or replace function verify_check_digit
        (engine_id_numeric IN NUMBER) return NUMBER DETERMINISTIC
    as
        engine_id CHAR := to_char(engine_id_numeric);
    begin
        if compute_check_character(substr(engine_id, 0, 6)) = substr(engine_id, 7, 1) then
            return 1;
        else
            return 0;
        end if;
end verify_check_digit;
